import React from 'react';
import './ModalStyles.css';

function groupByType(activities) {
  const grouped = {};
  activities.forEach(act => {
    if (!grouped[act.tipo]) grouped[act.tipo] = [];
    grouped[act.tipo].push(act);
  });
  return grouped;
}

export default function SettingsModal({ activities, open, onClose }) {
  if (!open) return null;
  const grouped = groupByType(activities);
  return (
    <div className="modal-bg">
      <div className="modal-glass settings-modal">
        <button className="modal-close" onClick={onClose}>&times;</button>
        <h2 className="modal-title">Ajustes de Actividades</h2>
        {Object.entries(grouped).map(([tipo, acts]) => (
          <section key={tipo} className="settings-section">
            <h3 className="settings-section-title">{tipo}</h3>
            <ul className="settings-list">
              {acts.map(act => (
                <li key={act.id || act.nombre} className="settings-item">
                  {act.nombre}
                </li>
              ))}
            </ul>
          </section>
        ))}
      </div>
    </div>
  );
}
