import React from 'react';
import CalendarHeatmap from 'react-calendar-heatmap';
import { Tooltip } from 'react-tooltip';
import 'react-calendar-heatmap/dist/styles.css';
import './CalendarModal.css';

export default function CalendarModal({ isOpen, onClose, completions }) {
  if (!isOpen) return null;

  // Convert completions (date → count) to heatmap data expected by the component
  const heatmapData = Object.entries(completions).map(([date, obj]) => ({
    date,
    completed: obj.completed,
    total: obj.total,
  }));

  const startDate = new Date();
  startDate.setMonth(startDate.getMonth() - 3);

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="calendar-modal" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Tu progreso</h2>
          <button className="close-button" onClick={onClose}>×</button>
        </div>
        <div className="calendar-container">
          <CalendarHeatmap
            startDate={startDate}
            endDate={new Date()}
            values={heatmapData}
            classForValue={(value) => {
              if (!value || !value.completed) return 'color-empty';
              // Map counts to 5-level scale similar a GitHub
              const c = value.completed;
              let level = 0;
              if (c >= 6) level = 4;       // very intenso
              else if (c >= 4) level = 3;  // alto
              else if (c >= 2) level = 2;  // medio
              else if (c === 1) level = 1; // bajo
              return `color-scale-${level}`;
            }}
            tooltipDataAttrs={(value) => {
              if (!value || !value.date) return {};
              const dateObj = new Date(value.date);
              const dateStr = dateObj.toLocaleDateString('es-ES', { day: '2-digit', month: '2-digit' });
              return {
                'data-tooltip-id': 'calendar-tooltip',
                'data-tooltip-content': `${value.completed}/${value.total} ${dateStr}`,
              };
            }}
            showWeekdayLabels={true}
          />
          <Tooltip id="calendar-tooltip" />
        </div>
      </div>
    </div>
  );
}
