import React from 'react';
import './FrequencyModal.css';

const FrequencyCard = ({ title, activity, tag }) => {
  if (!activity) return null;

  return (
    <div className={`frequency-card ${tag ? `tag-${tag}` : ''}`}>
      <h3>{title}</h3>
      <p className="activity-name">{activity}</p>
      {tag && <span className="tag-indicator">{tag}</span>}
    </div>
  );
};

export default function FrequencyModal({ isOpen, onClose, activities }) {
  if (!isOpen) return null;

  // L√≥gica para calcular las estad√≠sticas
  const getMostFrequent = (tag = null) => {
    // Filtrar actividades por tag si se especifica
    const filteredActivities = tag 
      ? activities.filter(act => act.tag === tag)
      : activities;

    // Contar frecuencias
    const frequencyMap = {};
    filteredActivities.forEach(act => {
      frequencyMap[act.actividad] = (frequencyMap[act.actividad] || 0) + 1;
    });

    // Encontrar la actividad m√°s frecuente
    let maxCount = 0;
    let mostFrequent = null;
    Object.entries(frequencyMap).forEach(([activity, count]) => {
      if (count > maxCount) {
        maxCount = count;
        mostFrequent = activity;
      }
    });

    return mostFrequent;
  };

  // Obtener datos
  const mostFrequentOverall = getMostFrequent();
  const mostFrequentPrimary = getMostFrequent('principal');
  const mostFrequentSecondary = getMostFrequent('secundaria');
  const mostFrequentLowPriority = getMostFrequent('baja');
  const mostFrequentPassive = getMostFrequent('pasivo');

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="frequency-modal" onClick={e => e.stopPropagation()}>
        <button className="modal-close-button" onClick={onClose}>√ó</button>
        <h2>Estad√≠sticas de Frecuencia</h2>

        <div className="frequency-stats">
          <div className="stat-card">
            <h3>üèÜ Actividad m√°s frecuente</h3>
            <p className="stat-value">{mostFrequentOverall || 'N/A'}</p>
          </div>
          <div className="stat-card">
            <h3>‚≠ê Principal m√°s frecuente</h3>
            <p className="stat-value">{mostFrequentPrimary || 'N/A'}</p>
          </div>
          <div className="stat-card">
            <h3>üî∂ Secundaria m√°s frecuente</h3>
            <p className="stat-value">{mostFrequentSecondary || 'N/A'}</p>
          </div>
          <div className="stat-card">
            <h3>üìâ Baja prioridad m√°s frecuente</h3>
            <p className="stat-value">{mostFrequentLowPriority || 'N/A'}</p>
          </div>
          <div className="stat-card">
            <h3>üéß Pasivo m√°s frecuente</h3>
            <p className="stat-value">{mostFrequentPassive || 'N/A'}</p>
          </div>
        </div>
      </div>
    </div>
  );
}
