import React from 'react';
import Activity from './Activity';

const DayView = ({ date, activities, onToggle, onPreviousWeek, onNextWeek }) => {
  // Function to get the start (Monday) and end (Sunday) of the week
  const getWeekRange = (date) => {
    const start = new Date(date);
    start.setDate(date.getDate() - date.getDay() + (date.getDay() === 0 ? -6 : 1)); // Monday
    const end = new Date(start);
    end.setDate(start.getDate() + 6); // Sunday
    return { start, end };
  };

  const getWeekNumber = (date) => {
    // Copy date so don't modify original
    const d = new Date(Date.UTC(date.getFullYear(), date.getMonth(), date.getDate()));
    // Set to nearest Thursday: current date + 4 - current day of the week
    // Make Sunday's day number 7
    d.setUTCDate(d.getUTCDate() + 4 - (d.getUTCDay() || 7));
    // Get first day of year
    const yearStart = new Date(Date.UTC(d.getUTCFullYear(),0,1));
    // Calculate full weeks to nearest Thursday
    const weekNo = Math.ceil(( ( (d - yearStart) / 86400000) + 1)/7);
    return weekNo;
  };

  const { start, end } = getWeekRange(date);
  const weekNumber = getWeekNumber(date);

  // Format dates as "DD MMM"
  const formatDate = (date) => {
    return date.toLocaleDateString('es-ES', { day: 'numeric', month: 'short' });
  };

  const weekRangeString = `${formatDate(start)} - ${formatDate(end)}`;

  return (
    <div className="day-view">
      <div className="week-navigation">
        <button onClick={onPreviousWeek}>Semana anterior</button>
        <h2>Semana {weekNumber}: {weekRangeString}</h2>
        <button onClick={onNextWeek}>Siguiente semana</button>
      </div>
      <h3>{date.toLocaleDateString('es-ES', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</h3>
      <div className="activities-container">
        {activities.map(activity => (
          <Activity 
            key={activity.id} 
            activity={activity} 
            onToggle={onToggle} 
          >
            <li>
              <input 
                type="checkbox" 
                checked={activity.completado} 
                onChange={() => onToggle(activity.id)} 
              />
              <div className="activity-content">
                <div className="activity-top">
                  {activity.icono && <span className="activity-icon" style={{ fontSize: '1.5em', marginRight: '8px' }}>{activity.icono}</span>}
                  <span className="tag">{activity.tipo}</span>
                </div>
                <span className={activity.completado ? 'completed' : ''}>
                  {activity.actividad}
                </span>
              </div>
            </li>
          </Activity>
        ))}
      </div>
    </div>
  );
};

export default DayView;
