import React from 'react';

const Activity = ({ activity, onToggle }) => {
  const handleClick = (e) => {
    // Prevent event bubbling to parent elements
    e.stopPropagation();

    // Only toggle if not clicking on the icon or tag
    if (!e.target.closest('.activity-icon, .activity-type')) {
      onToggle(activity.id);
    }
  };

  return (
    <div 
      className={`activity ${activity.completado ? 'completed' : ''}`} 
      onClick={handleClick}
    >
      <div className="activity-checkbox-wrapper">
        <input 
          type="checkbox" 
          checked={activity.completado} 
          onChange={() => onToggle(activity.id)}
          className="activity-checkbox"
        />
        <span className="custom-checkbox">
          {activity.completado && '✓'}
        </span>
      </div>
      <div className="activity-info">
        <span className="activity-type">{activity.tipo}</span>
        <span className="activity-text">
          {activity.icono && (
            <span className="activity-icon" style={{marginRight: '8px'}}>
              {activity.icono}
            </span>
          )}
          {activity.actividad}
        </span>
      </div>
    </div>
  );
};

export default Activity;
