import React from 'react';

const Activity = ({ activity, onToggle }) => {
  const handleToggle = (e) => {
    e.stopPropagation();
    onToggle(activity.id);
  };

  return (
    <div 
      className="activity" 
      onClick={handleToggle}
      style={{ cursor: 'pointer' }}
    >
      <div className="activity-checkbox" onClick={(e) => e.stopPropagation()}>
        <input 
          type="checkbox" 
          checked={activity.completado} 
          onChange={handleToggle}
          onClick={(e) => e.stopPropagation()}
        />
      </div>
      <div className="activity-info" onClick={handleToggle}>
        <span className="activity-type" style={{marginBottom: '10px'}}>{activity.tipo}</span>
        <span className={`activity-text ${activity.completado ? 'completed' : ''}`}>
          {activity.icono && <span style={{marginRight: '8px', pointerEvents: 'none'}}>{activity.icono}</span>}
          {activity.actividad}
        </span>
      </div>
    </div>
  );
};

export default Activity;
