import React from 'react';

const DayView = ({ day, activities, onToggle }) => (
  <div className="day-view">
    <h3>{day}</h3>
    <ul className="activities-list">
      {activities.map(activity => (
        <li key={activity.id} className="activity-item">
          <input
            type="checkbox"
            checked={activity.completado}
            onChange={() => onToggle(activity.id)}
            className="activity-checkbox"
          />
          <div className="activity-content">
            <div className="activity-top">
              {activity.prioridad === 'alta' && <span className="priority-icon">⚠️</span>}
              <span className="tag">{activity.tipo}</span>
            </div>
            <div className={`activity-name ${activity.completado ? 'completed' : ''}`}>
              {activity.actividad}
            </div>
          </div>
        </li>
      ))}
    </ul>
  </div>
);

export default DayView;
