import React from 'react';
import CalendarHeatmap from 'react-calendar-heatmap';
import { Tooltip } from 'react-tooltip';
import 'react-calendar-heatmap/dist/styles.css';
import './CalendarModal.css';

export default function CalendarModal({ isOpen, onClose, completions }) {
  if (!isOpen) return null;

  // Convert completions to heatmap data
  const heatmapData = Object.entries(completions).map(([date, activities]) => ({
    date,
    count: Object.values(activities).filter(Boolean).length
  }));

  const startDate = new Date();
  startDate.setMonth(startDate.getMonth() - 3);

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="calendar-modal" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Tu progreso</h2>
          <button className="close-button" onClick={onClose}>Ã—</button>
        </div>
        <div className="calendar-container">
          <CalendarHeatmap
            startDate={startDate}
            endDate={new Date()}
            values={heatmapData}
            classForValue={(value) => {
              if (!value) return 'color-empty';
              return `color-scale-${Math.min(4, value.count)}`;
            }}
            tooltipDataAttrs={(value) => ({
              'data-tooltip-id': 'calendar-tooltip',
              'data-tooltip-content': value.date ? `Actividades: ${value.count} el ${value.date}` : '',
            })}
            showWeekdayLabels={true}
          />
          <Tooltip id="calendar-tooltip" />
        </div>
      </div>
    </div>
  );
}
