import React, { useState } from 'react';
import CalendarHeatmap from 'react-calendar-heatmap';
import { Tooltip } from 'react-tooltip';
import { format } from 'date-fns';
import 'react-calendar-heatmap/dist/styles.css';
import './CalendarModal.css';

export default function CalendarModal({ isOpen, onClose, completions }) {
  const [viewMode, setViewMode] = useState('year'); // 'year', 'month', 'week'

  if (!isOpen) return null;

  // Calculate date ranges based on view mode
  const now = new Date();
  let startDate = new Date();

  switch(viewMode) {
    case 'year':
      startDate.setFullYear(startDate.getFullYear() - 1);
      break;
    case 'month':
      startDate.setMonth(startDate.getMonth() - 1);
      break;
    case 'week':
      startDate.setDate(startDate.getDate() - 7);
      break;
    default:
      startDate.setFullYear(startDate.getFullYear() - 1);
  }

  // Convert completions (date → count) to heatmap data expected by the component
  const heatmapData = Object.entries(completions).map(([date, obj]) => ({
    date,
    completed: obj.completed,
    total: obj.total,
  }));

  // Calculate cell size based on view mode
  const baseSize = 12;
  let cellSize = baseSize;
  let gutterSize = 6;

  if (viewMode === 'month') {
    cellSize = Math.round(baseSize * 0.5); // 50% smaller
    gutterSize = 3;
  } else if (viewMode === 'week') {
    cellSize = Math.round(baseSize * 0.3); // 70% smaller
    gutterSize = 1;
  }

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="calendar-modal" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Tu progreso</h2>
          <div className="view-buttons">
            <button 
              className={`view-button ${viewMode === 'year' ? 'active' : ''}`}
              onClick={() => setViewMode('year')}
            >
              Año
            </button>
            <button 
              className={`view-button ${viewMode === 'month' ? 'active' : ''}`}
              onClick={() => setViewMode('month')}
            >
              Mes
            </button>
            <button 
              className={`view-button ${viewMode === 'week' ? 'active' : ''}`}
              onClick={() => setViewMode('week')}
            >
              Semana
            </button>
          </div>
          <button className="close-button" onClick={onClose}>×</button>
        </div>
        <div className="calendar-container">
          <CalendarHeatmap
            startDate={startDate}
            endDate={now}
            values={heatmapData}
            classForValue={(value) => {
              if (!value || !value.completed) return 'color-empty';
              // Map counts to 5-level scale similar a GitHub
              const c = value.completed;
              let level = 0;
              if (c >= 6) level = 4;       // very intenso
              else if (c >= 4) level = 3;  // alto
              else if (c >= 2) level = 2;  // medio
              else if (c === 1) level = 1; // bajo
              return `color-scale-${level}`;
            }}
            tooltipDataAttrs={(value) => {
              if (!value || !value.date) return {};
              const dateObj = new Date(value.date);
              const dateStr = format(dateObj, 'dd-MM');
              return {
                'data-tooltip-id': 'calendar-tooltip',
                'data-tooltip-content': `${dateStr} Actividades: ${value.completed} de ${value.total}`,
              };
            }}
            showWeekdayLabels={true}
            horizontal={true}
            weekdayLabels={['Dom', 'Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb']}
            gutterSize={gutterSize}
            cellSize={cellSize}
          />
          <Tooltip id="calendar-tooltip" />
        </div>
      </div>
    </div>
  );
}
