import React from 'react';
import CalendarHeatmap from 'react-calendar-heatmap';
import 'react-calendar-heatmap/dist/styles.css';
import './CalendarModal.css';

export default function CalendarModal({ isOpen, onClose }) {
  if (!isOpen) return null;

  // Temporary mock data for the heatmap
  const startDate = new Date();
  startDate.setMonth(startDate.getMonth() - 3);

  const values = [
    { date: '2025-06-01', count: 1 },
    { date: '2025-06-02', count: 4 },
    { date: '2025-06-03', count: 2 },
    { date: '2025-06-05', count: 3 },
    { date: '2025-06-06', count: 4 },
    { date: '2025-06-10', count: 1 },
    { date: '2025-06-15', count: 3 },
    { date: '2025-06-20', count: 2 },
    { date: '2025-06-25', count: 4 },
    { date: '2025-06-30', count: 1 },
  ];

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="calendar-modal" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Tu progreso</h2>
          <button className="close-button" onClick={onClose}>Ã—</button>
        </div>
        <div className="calendar-container">
          <CalendarHeatmap
            startDate={startDate}
            endDate={new Date()}
            values={values}
            classForValue={(value) => {
              if (!value) {
                return 'color-empty';
              }
              return `color-scale-${value.count}`;
            }}
            tooltipDataAttrs={(value) => ({
              'data-tooltip': value.date ? `Actividades: ${value.count} el ${value.date}` : '',
            })}
            showWeekdayLabels={true}
          />
        </div>
      </div>
    </div>
  );
}
