import React from 'react';
import ModalActivity from './ModalActivity';

export default function FrequencyModal({ isOpen, onClose, activities }) {
  if (!isOpen) return null;

  // L√≥gica para calcular las estad√≠sticas
  const getMostFrequent = (tag = null) => {
    // Filtrar actividades por tag si se especifica
    const filteredActivities = tag 
      ? activities.filter(act => act.tag === tag)
      : activities;

    // Contar frecuencias
    const frequencyMap = {};
    filteredActivities.forEach(act => {
      frequencyMap[act.actividad] = (frequencyMap[act.actividad] || 0) + 1;
    });

    // Encontrar la actividad m√°s frecuente
    let maxCount = 0;
    let mostFrequent = null;
    Object.entries(frequencyMap).forEach(([activity, count]) => {
      if (count > maxCount) {
        maxCount = count;
        mostFrequent = activity;
      }
    });

    return mostFrequent;
  };

  // Obtener datos
  const mostFrequentOverall = getMostFrequent();
  const mostFrequentPrimary = getMostFrequent('principal');
  const mostFrequentSecondary = getMostFrequent('secundaria');
  const mostFrequentLowPriority = getMostFrequent('baja');
  const mostFrequentPassive = getMostFrequent('pasivo');

  // Crear objetos de actividad simulados para cada categor√≠a
  const statsActivities = [
    {
      id: 'overall',
      actividad: mostFrequentOverall || 'N/A',
      tipo: 'General',
      completado: false,
      emoji: 'üèÜ'
    },
    {
      id: 'primary',
      actividad: mostFrequentPrimary || 'N/A',
      tipo: 'Principal',
      completado: false,
      emoji: 'üìä'
    },
    {
      id: 'secondary',
      actividad: mostFrequentSecondary || 'N/A',
      tipo: 'Secundaria',
      completado: false,
      emoji: 'üìö'
    },
    {
      id: 'low',
      actividad: mostFrequentLowPriority || 'N/A',
      tipo: 'Baja prioridad',
      completado: false,
      emoji: '‚è≥'
    },
    {
      id: 'passive',
      actividad: mostFrequentPassive || 'N/A',
      tipo: 'Pasivo',
      completado: false,
      emoji: 'üß†'
    }
  ];

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content glassmorphism" onClick={e => e.stopPropagation()}>
        <button className="modal-close-button" onClick={onClose}>√ó</button>
        <h2>Estad√≠sticas de Frecuencia</h2>

        <div className="modal-activities-container">
          {statsActivities.map(activity => (
            <ModalActivity
              key={activity.id}
              activity={activity}
              customLabel={activity.emoji + ' ' + activity.actividad}
            />
          ))}
        </div>
      </div>
    </div>
  );
}
