import React, { useMemo, useState, useEffect, useRef } from 'react';
import { FaTimes, FaPlus, FaSave, FaSmile } from 'react-icons/fa';
import EmojiPicker from 'emoji-picker-react';
import './SettingsModal.css';
import { defaultActivities } from '../data/defaultActivities';

// Group activities by their type
const groupActivitiesByType = (activities) => {
  const grouped = {};

  // Process each day's activities
  Object.values(activities).forEach(dayActivities => {
    dayActivities.forEach(activity => {
      if (!grouped[activity.tipo]) {
        grouped[activity.tipo] = [];
      }
      // Only add if not already in the array (to avoid duplicates)
      if (!grouped[activity.tipo].some(a => a.actividad === activity.actividad)) {
        grouped[activity.tipo].push(activity);
      }
    });
  });

  return grouped;
};

// Available activity types for the dropdown
const ACTIVITY_TYPES = [
  'Algoritmos',
  'Actividad Principal',
  'Secundaria',
  'Menor Prioridad',
  'Conocimiento Pasivo'
];

// Available emoji icons for activities
const EMOJI_ICONS = ['ðŸ“±', 'ðŸ’»', 'ðŸŒ', 'âš¡', 'ðŸ¤–', 'ðŸ”„', 'â˜ï¸', 'âš™ï¸', 'ðŸ“š', 'ðŸ”§', 'ðŸ“Š', 'ðŸŒ±'];

export default function SettingsModal({ isOpen, onClose, onLogout, onAddActivity }) {
  const [activitiesByType, setActivitiesByType] = useState({});
  const [isAddingActivity, setIsAddingActivity] = useState(false);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const emojiPickerRef = useRef(null);
  const [newActivity, setNewActivity] = useState({
    actividad: '',
    tipo: 'Algoritmos',
    icono: 'ðŸ“±'
  });

  // Load activities and group them by type
  // Cerrar el selector de emojis al hacer clic fuera
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (emojiPickerRef.current && !emojiPickerRef.current.contains(event.target)) {
        setShowEmojiPicker(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  useEffect(() => {
    setActivitiesByType(groupActivitiesByType(defaultActivities));
  }, []);

  const handleEmojiClick = (emojiData) => {
    setNewActivity(prev => ({
      ...prev,
      icono: emojiData.emoji
    }));
    setShowEmojiPicker(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewActivity(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddActivity = () => {
    if (!newActivity.actividad.trim()) return;

    const activityToAdd = {
      id: `custom-${Date.now()}`,
      actividad: newActivity.actividad.trim(),
      tipo: newActivity.tipo,
      icono: newActivity.icono,
      completado: false
    };

    // Update local state
    const updatedActivities = { ...activitiesByType };
    if (!updatedActivities[activityToAdd.tipo]) {
      updatedActivities[activityToAdd.tipo] = [];
    }
    updatedActivities[activityToAdd.tipo].push(activityToAdd);
    setActivitiesByType(updatedActivities);

    // Notify parent component
    if (onAddActivity) {
      onAddActivity(activityToAdd);
    }

    // Reset form
    setNewActivity({
      actividad: '',
      tipo: 'Algoritmos',
      icono: 'ðŸ“±'
    });
    setIsAddingActivity(false);
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="settings-modal" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h2>ConfiguraciÃ³n</h2>
          <button className="modal-close-button" onClick={onClose}>
            <FaTimes />
          </button>
        </div>

        <div className="settings-content">
          <div className="settings-actions">
            {!isAddingActivity ? (
              <button 
                className="add-activity-button"
                onClick={() => setIsAddingActivity(true)}
              >
                <FaPlus /> Agregar Actividad
              </button>
            ) : (
              <div className="add-activity-form">
                <div className="form-group">
                  <input
                    type="text"
                    name="actividad"
                    value={newActivity.actividad}
                    onChange={handleInputChange}
                    placeholder="Nombre de la actividad"
                    className="activity-input"
                    autoFocus
                  />

                  <select
                    name="tipo"
                    value={newActivity.tipo}
                    onChange={handleInputChange}
                    className="activity-select"
                  >
                    {ACTIVITY_TYPES.map(type => (
                      <option key={type} value={type}>{type}</option>
                    ))}
                  </select>

                  <div className="icon-input-container" ref={emojiPickerRef}>
                    <div 
                      className="emoji-picker-trigger"
                      onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                    >
                      <span className="icon-preview">{newActivity.icono}</span>
                      <FaSmile className="emoji-icon" />
                    </div>

                    {showEmojiPicker && (
                      <div className="emoji-picker-container">
                        <EmojiPicker
                          onEmojiClick={handleEmojiClick}
                          autoFocusSearch={false}
                          width="100%"
                          height={300}
                          searchPlaceholder="Buscar emoji..."
                          skinTonesDisabled
                          previewConfig={{
                            showPreview: false
                          }}
                        />
                      </div>
                    )}
                  </div>

                  <div className="form-actions">
                    <button 
                      className="save-button"
                      onClick={handleAddActivity}
                      disabled={!newActivity.actividad.trim()}
                    >
                      <FaSave /> Guardar
                    </button>
                    <button 
                      className="cancel-button"
                      onClick={() => setIsAddingActivity(false)}
                    >
                      Cancelar
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>

          {Object.entries(activitiesByType).map(([type, activities]) => (
            <div key={type} className="activity-type-section">
              <h3 className="activity-type-title">{type}</h3>
              <div className="activity-grid">
                {activities.map(activity => (
                  <div key={activity.id} className="activity-card">
                    <span className="activity-icon">{activity.icono}</span>
                    <span className="activity-name">{activity.actividad}</span>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
